import useForm from '../lib/useForm';

export default function CreateProduct() {
  const { inputs, handleChange, clearForm } = useForm({
    name: 'Nice shoes',
    price: 34234,
    description: 'Best shoes',
  });

  return (
    <form>
      <label htmlFor="name">
        Name
        <input
          type="text"
          id="name"
          name="name"
          placeholder="Name"
          value={inputs.name}
          onChange={handleChange}
        />
      </label>
      <label htmlFor="price">
        Price
        <input
          type="number"
          id="price"
          name="price"
          placeholder="price"
          value={inputs.price}
          onChange={handleChange}
        />
      </label>

      <button type="button" onClick={clearForm}>
        Clear Form
      </button>
    </form>
  );
}
